{
    "type":"button",
    "label":"打开弹窗表单",
    "level": "primary",
    "actionType":"dialog",
    "dialog":{
        "type":"dialog",
        "title":"系统提示",
        "closeOnEsc": true,
        "body": [
            {
                "type":"form",
                "title":"表单",
                "api":"/api/mock2/form/saveForm",
                "body":[
                    {
                        "label":"字段a",
                        "type":"input-text",
                        "name":"a",
                        "required":true
                    },
                    {
                        "name":"b",
                        "label":"字段b",
                        "type":"input-text",
                        "validations": {
                          "minimum": 1,
                          "isNumeric": true,
                          "isInt": true
                        },
                        "required": false
                    },
                    {
                        "name":"c",
                        "label":"字段c",
                        "type":"input-text"
                    },
                    {
                        "name":"d",
                        "label":"字段d",
                        "type":"input-text",
                        "required":true
                    }
                ]
            }
        ],
        "actions":[
            {
                "type":"submit",
                "label":"提交-校验字段b",
                "actionType":"submit",
                "required":["b"],
                "level": "info"
            },
            {
                "type":"submit",
                "label":"提交-校验字段b, c",
                "actionType":"submit",
                "required":["b", "c"],
                "level": "info"
            }
        ]
    }
}

