          "label": "提交",
          "level": "primary",
          "visibleOn": "${!isStatic}",
          "onEvent": {
            "click": {
              "actions": [
                {
                  "actionType": "setValue",
                  "componentId": "allFormSwitch",
                  "args": {
                    "value": {
                      "isStatic": true
                    }
                  }
                },
                {
                  "actionType": "static",
                  "componentId": "allFormSwitch"
                }
              ]
            }
          }
        },
