          "label": "编辑",
          "level": "primary",
          "visibleOn": "${isStatic}",
          "onEvent": {
            "click": {
              "actions": [
                {
                  "actionType": "setValue",
                  "componentId": "allFormSwitch",
                  "args": {
                    "value": {
                      "isStatic": false
                    }
                  }
                },
                {
                  "actionType": "nonstatic",
                  "componentId": "allFormSwitch"
                }
              ]
            }
          }
        }
      ]
    },
  ],
  "actions": []
}

